var http = require("http");
var fs = require("fs");
http.createServer(function(req,res){
    var url = "."+req.url;
    fs.readFile(url,function(error,data){
        if(error){
            res.write("您请求的地址有误");
        }else{
            res.write(data);
        }
        res.end();
    })

}).listen(8080);